mapper.update:
UPDATE transaction
receiver_name = #{receiverName:VARCHAR}, receiver_phone = #{receiverPhone:VARCHAR}, demailaddr = #{divyAddr:VARCHAR}, dlvy_request = #{divyRequest:VARCHAR}, dlvy_date = #{divyDate:DATE}
WHERE tran_no = #{tranNo}

mapper.select:
  		SELECT *
	  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	  					FROM		(	SELECT t.tran_no , p.prod_no, p.prod_name, p.price, t.buyer_id, t.receiver_name, t.receiver_phone, t.tran_status_code 
											FROM transaction t, product p, users u
											
												t.prod_no = p.prod_no
												AND t.buyer_id = u.user_id
												AND t.buyer_id = #{searchKeywordInPurchase}
												
													
										 				AND t.tran_no = #{searchKeyword}
													
													
										 				AND p.prod_no = #{searchKeyword}
													
													
										 				AND t.tran_status_code = #{searchKeyword}
													
												
											
											ORDER BY tran_no ) inner_table
						WHERE ROWNUM <= #{endRowNum} )
		WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  		SELECT *
	  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	  					FROM		(	SELECT t.tran_no , p.prod_no, p.prod_name, p.price, p.reg_date, t.order_data, t.tran_status_code
											FROM transaction t, product p
											
												t.prod_no = p.prod_no
												AND t.tran_status_code NOT IN ('003')
												
													
										 				AND t.tran_no = #{searchKeyword}
													
													
										 				AND p.prod_no = #{searchKeyword}
													
													
										 				AND t.tran_status_code = #{searchKeyword}
													
												
											
											ORDER BY tran_no ) inner_table
						WHERE ROWNUM <= #{endRowNum} )
		WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT t.tran_no , p.prod_no, p.prod_name, p.price, p.reg_date, t.tran_status_code
					FROM transaction t, product p, users u
					
						t.prod_no = p.prod_no
						AND t.buyer_id = u.user_id
						AND t.buyer_id = #{searchKeywordInPurchase}
						
							
				 				AND t.tran_no = #{searchKeyword}
							
							
				 				AND p.prod_no = #{searchKeyword}
							
							
				 				AND t.tran_status_code = #{searchKeyword}
							
						
					) countTable

  	SELECT COUNT(*)
  	FROM(	SELECT t.tran_no , p.prod_no, p.prod_name, p.price, p.reg_date, t.tran_status_code
					FROM transaction t, product p
					
						t.prod_no = p.prod_no
						AND t.tran_status_code NOT IN ('003')
						
							
				 				t.tran_no = #{searchKeyword}
							
							
				 				p.prod_no = #{searchKeyword}
							
							
				 				t.tran_status_code = #{searchKeyword}
							
						
					) countTable

SELECT MAX(tran_no)
FROM transaction
WHERE buyer_id = #{ buyerId }
